import "core-js/modules/web.url-search-params.size.js";
import { ArrowRight, Edit } from '@element-plus/icons-vue';
import { ref, onMounted, reactive } from 'vue';
import axios from 'axios';
// 图片上传封装方法
import upload from '@/http/service/upload.js';
// 图片上传封装组件
import Upload from '@/components/upload/Upload.vue';
import { ElMessage } from 'element-plus';
export default {
  __name: 'Focus',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    let tableData = ref([]);
    const editForm = reactive({
      title: '',
      img: '',
      href: '/home',
      file: ''
    });
    const options = [{
      label: 'three',
      value: '/home'
    }, {
      label: 'camera',
      value: '/home'
    }];

    // 图片上传change事件
    const handleChange = file => {
      // 对图片进行处理
      editForm.img = URL.createObjectURL(file);
      editForm.file = file;
    };
    const getFocus = async () => {
      const res = await axios.get('/adminapi/focus');
      if (res.status !== 201) return ElMessage.error('获取列表失败');
      tableData.value = res.data.data;
    };
    const focusEdit = () => {};
    onMounted(() => {
      getFocus();
    });
    const __returned__ = {
      get tableData() {
        return tableData;
      },
      set tableData(v) {
        tableData = v;
      },
      editForm,
      options,
      handleChange,
      getFocus,
      focusEdit,
      get ArrowRight() {
        return ArrowRight;
      },
      get Edit() {
        return Edit;
      },
      ref,
      onMounted,
      reactive,
      get axios() {
        return axios;
      },
      get upload() {
        return upload;
      },
      Upload,
      get ElMessage() {
        return ElMessage;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
